import React from 'react';
import './App.css';
import Buttons from './Buttons'
import Question from './Question'


// async function get_tweets() {
//   let result = await (await fetch('http://127.0.0.1:5000/tweets')).json();
//   // let json = await result.json()
//   this.setState({questions:result})
//   return result;
// }


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: 'TweetMatch'
    } 
    this.click = this.submit.bind(this); 
  }

  componentDidMount() {
    fetch('http://127.0.0.1:5000/tweets').then(response => response.json()).then(data => this.setState({questions:data}));
  }

  submit() {
    console.log('sub')
    if (this.state.questions) {
      console.log('in')
      return display_questions(this.state.questions,false);
    }
  }

  render() {
    return (
          <div className = 'App-body'>
            <header className = 'App-header'>
                {this.state.title}
            </header>
            <ul className='sidebar'>
                <li><a href='#1'>Question 1</a></li>
                <li><a href='#2'>Question 2</a></li>
                <li><a href='#3'>Question 3</a></li>
                <li><a href='#4'>Question 4</a></li>
                <li><a href='#5'>Question 5</a></li>
                <li><a href='#6'>Question 6</a></li>
                <li><a href='#7'>Question 7</a></li>
                <li><a href='#8'>Question 8</a></li>
                <li><a href='#9'>Question 9</a></li>
                <li><a href='#10'>Question 10</a></li>
                <button className='submit' onClick = {() => this.submit()}> submit answers</button>
            </ul>

            {/* {this.state.questions && this.state.questions.map((question, index) => display_question(question, index))} */}
            {this.state.questions && display_questions(this.state.questions, true)}
          </div>
    );
  }

}

function display_questions(questions, sub) {
  console.log('dqs',sub)
  return questions.map((question, index) => display_question(question, index,sub))
}

function display_question(question, num,sub) {
  // num && console.log(num+1)
  console.log('dq',sub)
  var answer = question.answer
  var options = question.options
  var tweet = question.tweet
  return (
    // <div id={num+1} className='stack'>
    //   <header className = 'q-header'>
    //       Question {num+1}
    //   </header>
    //   <div className = 'question'>
    //     <div className='tweet'>
    //       {tweet}
    //     </div>
    //     <Buttons options = {options}></Buttons>
    //   </div>
    // </div>
    <Question tweet = {tweet} options = {options} answer = {answer} num={num} submitted={sub}></Question>
  )
}



export default App;
