import React from 'react';
import './App.css';
import Button from './Button'

export default class Buttons extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            selected: [false,false,false,false],
            options: props.options
        }
        this.click = this.click.bind(this);
    }

    click(num) {
        let sel = [...this.state.selected]
        for (var i = 0; i < sel.length; i++) {
            sel[i] = i===num ? true : false; 
          }
        this.setState({selected: sel});
    }

    render() {
        return (
            <div className = 'options'>
                <Button text= {this.state.options[0]} selected = {this.state.selected[0]} onClick = {this.click(0)}></Button>
                <Button text= {this.state.options[1]}></Button>
                <Button text= {this.state.options[2]}></Button>
                <Button text= {this.state.options[3]}></Button>
            </div>
        )
    }

}
