import React from 'react';
import './App.css';
import Buttons from './Buttons'

export default class Question extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            tweet: props.tweet,
            options: props.options,
            answer: props.answer,
            num: props.num,
            submitted: props.submitted
        }
    }

    get_buttons() {
        return <Buttons num = {this.state.num} side = {() => this.props.side} inc = {() => this.props.inc} options = {this.state.options} ans = {this.state.answer} submitted = {this.props.submitted} correct = {this.props.correct}></Buttons>
    }


    render() {
        // console.log(this.props.inc)
        return (
            <div id={this.state.num+1} className='stack'>
                <header className = 'q-header'>
                    Question {this.state.num+1}
                </header>
                <div className = 'question'>
                    <div className='tweet'>
                    {this.state.tweet}
                    </div>
                    {/* <Buttons num = {this.state.num} side = {() => this.props.side} inc = {this.props.inc} options = {this.state.options} ans = {this.state.answer} submitted = {this.props.submitted} correct = {this.props.correct}></Buttons> */}
                    {this.get_buttons()}
                </div>
            </div>
        )
    }
 }
