import React from 'react';
import './App.css';

export default class Button extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            selected : false,
            text : props.text,
            click : props.onClick
        }
    }

    changeButtoncolour() {
        var btn = document.getElementById(this.id);
        console.log(btn);
        if (btn) {
          console.log('in');
          btn.style.backgroundColor = 'black';
          btn.style.color = 'white';
        }
    }

    render() {
        return (
            <button className='option' onClick = {this.changeButtoncolour}>@{this.state.text}</button>
        )
    }
}
