//   // let json = await result.json()
//   this.setState({questions:result})
//   return result;
// }


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: 'TweetMatch'
    }  
  }

  componentDidMount() {
    fetch('http://127.0.0.1:5000/tweets').then(response => response.json()).then(data => this.setState({questions:data}));
  }

  render() {
    return (
          <div className = 'App-body'>
                <header className = 'App-header'>
                    {this.state.title}
                </header>
                {this.state.questions && this.state.questions.map((question, index) => display_question(question, index))}
                <ul className='sidebar'>
                    <li>asa</li>
                </ul>
          </div>
    );
  }

}

function display_question(question, num) {
  // num && console.log(num+1)
  var answer = question.answer
  var options = question.options
  var tweet = question.tweet
  return (
    // <div className = 'App-body'>
    //   <header className = 'q-header'>
    //       Question 1
    //   </header>
    <div>
      <header className = 'q-header'>
          Question {num+1}
      </header>
      <div className = 'question'>
        <div className='tweet'>
          {tweet}
        </div>
        <div className = 'options'>
          <button className='option'>
            @{options[0]} 
          </button>
          <button className='option'>
            @{options[1]} 
          </button>
          <button className='option'>
            @{options[2]} 
          </button>
          <button className='option'>
            @{options[3]} 
          </button>
        </div>
      </div>
    </div>
  )


}

export default App;
