import React from 'react';
import './App.css';
import Question from './Question'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: 'TweetMatch',
      submitted: false,
      right: 0
    } 
  }

  componentDidMount() {
    fetch('http://127.0.0.1:5000/tweets').then(response => response.json()).then(data => this.setState({questions:data}));
  }

  submit() {
    this.setState({submitted: true})
  }

  inc_right() {
    this.setState({right: this.state.right+1})
  }

  makeSidebar() {
    let items = []
    for (let i = 1; i < 11; i++) {
      items.push(<li key= {i}><a href={`#${i}`}>Question {i}</a></li>)
    }
    return items
  }

  render() {
    if (!this.state.submitted) {
      return (
            <div className = 'App-body'>
              <header className = 'App-header'>
                  {this.state.title}
              </header>
              <ul className='sidebar'>
                  {this.makeSidebar()}
                  <button className='submit' onClick = {() => this.submit()}> submit answers</button>
              </ul>              
              {this.state.questions && display_questions(this.state.questions, false)}
            </div>
      );
    }
    else {
      return (
        <div className = 'App-body'>
          <header className = 'App-header'>
              {this.state.title}
          </header>
          <ul className='sidebar'>
              {this.makeSidebar()}
          </ul>
          {this.state.questions && display_questions(this.state.questions, true)}
          console.log(this.state.right)
        </div>
      )
    }
  }
}

function display_questions(questions, sub) {
  return questions.map((question, index) => display_question(question, index,sub))
}

function display_question(question, num,sub) {
  var answer = question.answer
  var options = question.options
  var tweet = question.tweet
  var cor = []
  for (let i = 0; i < options.length; i++)
  {
    cor.push(options[i]===answer)
  }
  return (
    <Question tweet = {tweet} options = {options} answer = {answer} num={num} submitted={sub} correct = {cor} inc  = {this.inc_right}></Question>
  )
}



export default App;
