import React from 'react';
import './App.css';
import Button from './Button'

export default class Buttons extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            items: [
                {
                    id: 0,
                    selected: false
                },
                {
                    id: 1,
                    selected: false
                },
                {
                    id: 2,
                    selected: false
                },
                {
                    id: 3,
                    selected: false
                }
            ],
            options: props.options
        }
    }



    render() {
        function changeButtoncolour() {
            console.log('ere')
            var btn = document.getElementById(this.id);
            console.log(btn);
            if (btn) {
              console.log('in');
              btn.style.backgroundColor = 'black';
              btn.style.color = 'white';
            }
        }

        return (
            <div className = 'options'>
                <Button id = '1' text= {this.state.options[0]} onClick = {changeButtoncolour}> 
                </Button>
                <button id = '2' className='option'>
                    @{this.state.options[1]} 
                </button>
                <button id = '3' className='option'>
                    @{this.state.options[2]} 
                </button>
                <button id = '4' className='option'>
                    @{this.state.options[3]} 
                </button>
            </div>
        )
    }

}
