import React from 'react';
import './App.css';
import Button from './Button'

export default class Buttons extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            selected: [false,false,false,false],
            options: props.options,
            ans: props.ans,
            correct: [false,false,false,false],
            submitted: props.submitted
        }
        this.click = this.click.bind(this);

        if (props.submitted) {
            console.log('dfgd')
            this.check_answer()
        }
    }

    click(num) {
        let sel = [...this.state.selected]
        for (let i = 0; i < sel.length; i++) {
            sel[i] = i===num ? !sel[i] : false; 
          }
        this.setState({selected: sel});
    }

    check_answer() {
        let cor = [...this.state.correct]
        for (let i = 0; i < 4; i++) {
            cor[i] = this.state.options[i] === this.state.ans;
        }
        this.setState({correct: cor});
    }

    render() {
        if (!this.state.submitted) {
            return (
                <div className = 'options'>
                    <Button text= {this.state.options[0]} selected = {this.state.selected[0]} click = {() => this.click(0)}></Button>
                    <Button text= {this.state.options[1]} selected = {this.state.selected[1]} click = {() => this.click(1)}></Button>
                    <Button text= {this.state.options[2]} selected = {this.state.selected[2]} click = {() => this.click(2)}></Button>
                    <Button text= {this.state.options[3]} selected = {this.state.selected[3]} click = {() => this.click(3)}></Button>
                </div>
            )
        }
        else {
            return (
                <div className = 'options'>
                    <Button text= {this.state.options[0]} selected = {this.state.selected[0]} correct = {this.state.correct[0]} submitted = {this.state.submitted}></Button>
                    <Button text= {this.state.options[1]} selected = {this.state.selected[1]} correct = {this.state.correct[1]} submitted = {this.state.submitted}></Button>
                    <Button text= {this.state.options[2]} selected = {this.state.selected[2]} correct = {this.state.correct[2]} submitted = {this.state.submitted}></Button>
                    <Button text= {this.state.options[3]} selected = {this.state.selected[3]} correct = {this.state.correct[3]} submitted = {this.state.submitted}></Button>
                </div>
            )
        }
    }

}
