import React from 'react';
import './App.css';

export default class Buttons extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            items: [
                {
                    id: 0,
                    selected: false
                },
                {
                    id: 1,
                    selected: false
                },
                {
                    id: 2,
                    selected: false
                },
                {
                    id: 3,
                    selected: false
                }
            ],
            ans: props
        }
    }

    changeButtoncolour(id) {
        var btn = document.getElementById(id);
        console.log(btn);
        if (btn) {
          console.log('in');
          btn.style.backgroundColor = 'black';
          btn.style.color = 'white';
        }

    }


    render() {
        console.log(this.state.ans[0])
        return (
            <div className = 'options'>
                <button id = '1' className='option' onClick = {() => this.changeButtoncolour}>
                    @{this.state.ans[0]} 
                </button>
                {/* <button id = '2' className='option'>
                    @{this.state.options[1]} 
                </button>
                <button id = '3' className='option'>
                    @{this.state.options[2]} 
                </button>
                <button id = '4' className='option'>
                    @{this.state.options[3]} 
                </button> */}
            </div>
        )
    }

}
