import React from 'react';
import './App.css';
import Question from './Question'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: 'TweetMatch',
      submitted: false,
      right: 0,
      sidebar: [],
      num_questions: 10
    } 
    this.updateSidebar = this.updateSidebar.bind(this);
    this.inc_right = this.inc_right.bind(this);
    this.add_sidebar()
  }

  add_sidebar() {
    for (let i = 0; i < this.state.num_questions; i++) {
      this.state.sidebar.push(false)
    }
  }
  componentDidMount() {
    fetch('http://127.0.0.1:5000/tweets').then(response => response.json()).then(data => this.setState({questions:data}));
  }

  submit() {
    this.setState({submitted: true})
  }

  inc_right = () => {
    console.log('incr')
    let temp = this.state.right+1
    this.setState({right: temp})
  }

  makeSidebar() {
    let items = []
    for (let i = 1; i < 11; i++) {
      items.push(<li key= {i}><a href={`#${i}`}>Question {i}</a></li>)
    }
    return items
  }

  updateSidebar(num) {
    console.log(this)
    let side = [...this.state.sidebar]
    side[num] = true
    this.setState({sidebar:side})
  }

  display_questions(sub) {
    // let x = [...this.state.questions[0]]
    return this.state.questions.map((question, index) => this.display_question(question, index, sub))
    // this.display_question(this.state.questions[0], 0, sub)
  }

  display_question(question, num,sub) {
    var answer = question.answer
    var options = question.options
    var tweet = question.tweet
    var cor = []
    for (let i = 0; i < options.length; i++)
    {
      cor.push(options[i]===answer)
    }
    return (
      <Question key = {num} tweet = {tweet} options = {options} answer = {answer} num={num} submitted={sub} correct = {cor} inc  = {this.inc_right}></Question>
    )
  }

  render() {
    if (!this.state.submitted) {
      return (
            <div className = 'App-body'>
              <header className = 'App-header'>
                  {this.state.title}
              </header>
              <ul className='sidebar'>
                  {this.makeSidebar()}
                  <button className='submit' onClick = {() => this.submit()}> submit answers</button>
              </ul>              
              {this.state.questions && this.display_questions(false)}
            </div>
      );
    }
    else {
      // console.log('out',this.state.sidebar)
      let x = this.state.questions && this.display_questions(this.state.questions, true)
      // console.log(this.state.right)
      return (
        <div className = 'App-body'>
          <header className = 'App-header'>
              {this.state.title}
          </header>
          <ul className='sidebar'>
              <div className = 'score'>{this.state.right}/10</div>
              {this.makeSidebar()}
          </ul>
          {/* {this.state.questions && this.display_questions(this.state.questions, true)} */}
          {x}
        </div>


      )
    }
  }
}

