import React from 'react';
import './App.css';
import Button from './Button'
import Buttons from './Buttons'

export default class Question extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            tweet: props.tweet,
            options: props.option,
            answer: props.answer,
            num: props.num
        }
    }

    render() {
        console.log(this.state)
        return (
            <div id={this.state.num+1} className='stack'>
              <header className = 'q-header'>
                  Question {this.state.num+1}
              </header>
              <div className = 'question'>
                <div className='tweet'>
                  {this.state.tweet}
                </div>
                <Buttons options = {this.state.options}></Buttons>
              </div>
            </div>
          )
    }
 }
