import React from 'react';
import './App.css';
// import Header from './header'


// async function get_tweets() {
//   let result = await (await fetch('http://127.0.0.1:5000/tweets')).json();
//   // let json = await result.json()
//   this.setState({questions:result})
//   return result;
// }


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: 'TweetMatch'
    }  
  }

  componentDidMount() {
    fetch('http://127.0.0.1:5000/tweets').then(response => response.json()).then(data => this.setState({questions:data}));
  }

  render() {
    return (
          <div className = 'App-body'>
                <header className = 'App-header'>
                    {this.state.title}
                </header>
                    <ol>
                      {this.state.questions && this.state.questions.map(display_question)}
                    </ol>
          </div>
    );
  }

}

function display_question(question) {
  var answer = question.answer
  var options = question.options
  var tweet = question.tweet
  return (
    <div className = 'App-center'>
        <div className='tweet'>
          {tweet}
        </div>
        <div style={{fontSize: '2vmin'}}>
          Who Tweeted it?
        </div>
        <div>
          <button>
            {options[0]}
          </button>
        </div>
    </div>
  )


}

export default App;
