import React from 'react';
import './App.css';

export default class Button extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            selected : false,
            text : props.text,
            click : props.click
        }
    }

    render() {
        // console.log(this.state)
        return (
            <button className={this.state.selected ? 'option-selected' : 'option'} onClick = {this.state.click}>@{this.state.text}</button>
        )
    }
}
