import React from 'react';
import './App.css';
import Button from './Button'

export default class Buttons extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            items: [
                {
                    id: 0,
                    selected: false
                },
                {
                    id: 1,
                    selected: false
                },
                {
                    id: 2,
                    selected: false
                },
                {
                    id: 3,
                    selected: false
                }
            ],
            options: props.options
        }
        this.click = this.click.bind(this);
    }

    click(num) {
        let items = [...this.state.items]
        for (let i = 0; i < items.length; i++) {
            items[i].selected = items[i].id===num ? true : false; 
          }
        this.setState({items: items});
        // console.log('in', sel)
    }

    render() {
        return (
            <div className = 'options'>
                <Button text= {this.state.options[0]} selected = {this.state.selected[0]} click = {() => this.click(0)}></Button>
                <Button text= {this.state.options[1]} selected = {this.state.selected[1]} click = {() => this.click(1)}></Button>
                <Button text= {this.state.options[2]} selected = {this.state.selected[2]} click = {() => this.click(2)}></Button>
                <Button text= {this.state.options[3]} selected = {this.state.selected[3]} click = {() => this.click(3)}></Button>
            </div>
        )
    }

}
