import React from 'react';
import './App.css';
import Button from './Button'

export default class Buttons extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            items: [
                {
                    id: 0,
                    selected: false
                },
                {
                    id: 1,
                    selected: false
                },
                {
                    id: 2,
                    selected: false
                },
                {
                    id: 3,
                    selected: false
                }
            ],
            options: props.options
        }
    }



    render() {
        // function changeButtoncolour() {
