        //     if (btn) {
        //       console.log('in');
        //       btn.style.backgroundColor = 'black';
        //       btn.style.color = 'white';
        //     }
        // }

        return (
            <div className = 'options'>
                <Button text= {this.state.options[0]}></Button>
                <Button text= {this.state.options[1]}></Button>
                <Button text= {this.state.options[2]}></Button>
                <Button text= {this.state.options[3]}></Button>
            </div>
        )
    }

}
